import React, { useContext, useEffect, useRef } from 'react';
import { CartContext } from './CartContext';

const ShoppingCart = ({ isBox2Visible, toggleBoxVisibility, handleCloseButtonClick }) => {
  const { cartItems, setCartItems } = useContext(CartContext);
  const totalSum = cartItems.reduce((sum, item) => sum + item.price, 0);

  const cartRemoveBtn = useRef(null);

  const removeItem = (index) => {
    const currentCartItems = [...cartItems];
    currentCartItems.splice(index, 1);
    setCartItems(currentCartItems);
  };

  return (
    <div className="shopping-bag">
      <button className="header-btn shopping-btn shopping-btn-layout" onClick={toggleBoxVisibility}>
        <img src="icons/bag-shopping-solid.svg" alt="Shopping Bag" className="icon shopping-btn" />
        <span className="shopping-btn"> Your Cart</span>
      </button>

      {isBox2Visible && (
        <div className="shopping-box pop-up" tabIndex="1">
          <div className="flex-container">
            <h3 className="your-items">Your Items:</h3>
            <button
              className="cart-close-btn cart-close-btn-layout"
              onClick={handleCloseButtonClick}
            >
              <img
                src="icons/xmark-solid.svg"
                alt="X Mark"
                className="icon cart-close-btn"
              />
              <span className="cart-close-btn"> Close Cart</span>
            </button>
          </div>
          {cartItems.length === 0 ? (
            <p>Shopping cart is empty</p>
          ) : (
            <>
              <div className="cart-item">
                {cartItems.map((item, index) => (
                  <div key={index} className="cart-flex-container">
                    <img
                      src={item.image}
                      alt={item.alt}
                      style={{ width: '100px', height: '100px' }}
                      className="cart-img"
                    />
                    <div className="cart-item-info">
                      {item.name}
                      <br />
                      <b>{item.price} kr</b>
                    </div>
                    <button
                      className="cart-remove-btn"
                      onClick={() => removeItem(index)}
                    >
                      Remove Item
                    </button>
                  </div>
                ))}
              </div>
              <p>Total sum: {totalSum} kr</p>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ShoppingCart;
