import React, { useState } from 'react';
import { useRecoilState } from 'recoil';
import { cartState } from './atom';

const Product = ({ product }) => {
    const [cartItems, setCartItems] = useRecoilState(cartState);

    const addToCart = () => {
        if (product && product.image) {
            setCartItems((prevItems) => [...prevItems, product]);
            console.log(product);
          } else {
            console.error('Invalid product or missing image property');
          }
    }
    return (
        <div className="product item">
            <div className="product-img-container">
                <img src={product.image} alt={product.alt} className="product-img"></img>
            </div>
            <h2>{product.name}</h2>
            <p className="product-price">Price: <b>{product.price} kr</b></p>
            <button className="cart-btn" onClick={addToCart}><img src="icons/plus-solid.svg" alt="Plus Mark"></img><span> Add to Cart</span></button>
        </div>
    )
}

export default Product;
