import React, { useContext, useEffect, useRef } from 'react';
import Product from './Product';
import { CartContext } from './CartContext';
// ... (import statements)

const ShoppingCart = ({ isBox2Visible, toggleBoxVisibility, handleCloseButtonClick }) => {
    const { cartItems, setCartItems } = useContext(CartContext);

    const removeItem = (index) => {
      const currentCartItems = [...cartItems];
      currentCartItems.splice(index, 1);
      setCartItems(currentCartItems);
    };

    // Calculate total sum whenever cart items change
    useEffect(() => {
      const newTotalSum = cartItems.reduce((sum, item) => sum + item.price, 0);
      setTotalSum(newTotalSum);
    }, [cartItems]);

    // Initial total sum calculation
    const initialTotalSum = cartItems.reduce((sum, item) => sum + item.price, 0);
    const [totalSum, setTotalSum] = React.useState(initialTotalSum);

    return (
      <div className="shopping-bag">
        {/* ... (your existing button and container divs) */}
        {isBox2Visible && (
          <div className="shopping-box pop-up" tabIndex="1">
            <div className="flex-container">
              <h3 className="your-items">Your Items:</h3>
              <button className="cart-close-btn cart-close-btn-layout" onClick={handleCloseButtonClick}>
                <img src="icons/xmark-solid.svg" alt="X Mark" className="icon cart-close-btn"></img>
                <span className="cart-close-btn"> Close Cart</span>
              </button>
            </div>
            {cartItems.length === 0 ? (
              <p className="cart-empty-msg">Your cart is empty</p>
            ) : (
              <>
                <div>
                  {cartItems.map((item, index) => (
                    <div key={index} className="cart-flex-container">
                      <img src={item.image} alt={item.alt} style={{ width: '100px', height: '100px' }} className="cart-img" />
                      <div className="cart-item-info">
                        {item.name}
                        {/* Remove the individual price display */}
                      </div>
                      <button className="cart-remove-btn" onClick={() => removeItem(index)}>
                        <img src="icons/xmark-solid.svg" alt="X Mark" className="icon cart-close-btn"></img>
                        Remove Item
                      </button>
                    </div>
                  ))}
                </div>
                <p>Total sum: {totalSum} kr</p>
              </>
            )}
          </div>
        )}
      </div>
    );
  };

  export default ShoppingCart;
