import React, { useState, useRef } from "react";
import Settings from './Settings';
import ShoppingCart from './ShoppingCart';
import { NavLink } from "react-router-dom";

const Header = () => {
    const [isBox1Visible, setBox1Visibility] = useState(false);
    const [isBox2Visible, setBox2Visibility] = useState(false);

    const [isHovered1, setIsHovered1] = useState(false);
    const [isHovered2, setIsHovered2] = useState(false);
    const [isHovered3, setIsHovered3] = useState(false);
    const [isHovered4, setIsHovered4] = useState(false);

    const shadowRef = useRef(null);

    const toggleBoxVisibility = (event) => {
        const target = event.target;

        if(target.classList.contains("accessibility-btn")) {

            if(isBox1Visible === false) {
                setBox1Visibility((prevVisibility) => !prevVisibility);
                shadowRef.current.classList.add("shadow-box");

            } else if(isBox1Visible === true) {
                setBox1Visibility(false);
                shadowRef.current.classList.remove("shadow-box");

            }

        } else if(target.classList.contains("shopping-btn")) {

            if(isBox2Visible === false) {
                setBox2Visibility((prevVisibility) => !prevVisibility);
                shadowRef.current.classList.add("shadow-box");

            } else if(isBox2Visible === true) {
                setBox2Visibility(false);
                shadowRef.current.classList.remove("shadow-box");
            }

        }
    };

    const handleCloseButtonClick = (event) => {
        const target = event.target;

        if(target.classList.contains("close-btn") && isBox1Visible === true) {
            setBox1Visibility(false);
            shadowRef.current.classList.remove("shadow-box");

        } else if(target.classList.contains("cart-close-btn") && isBox2Visible === true) {
            setBox2Visibility(false);
            shadowRef.current.classList.remove("shadow-box");
        }
    };

    const handleMouseEnter = (dropdownNbr) => {
        switch (dropdownNbr) {
            case 1:
                setIsHovered1(true);
                break;
            case 2:
                setIsHovered2(true);
                break;
            case 3:
                setIsHovered3(true);
                break;
            case 4:
                setIsHovered4(true);
                break;
            default:
                break;
        }
    }

    const handleMouseLeave = (dropdownNbr) => {
        switch (dropdownNbr) {
            case 1:
                setIsHovered1(true);
                break;
            case 2:
                setIsHovered2(true);
                break;
            case 3:
                setIsHovered3(true);
                break;
            case 4:
                setIsHovered4(true);
                break;
            default:
                break;
        }
    }

    /*
              <ul className="navbar">
                    <li>
                        <NavLink to="/brushes">Brushes</NavLink>
                    </li>
                    <li>
                        <NavLink to="/paint">Paint</NavLink>
                    </li>
                    <li>
                        <NavLink to="/paper">Paper</NavLink>
                    </li>
                    <li>
                        <NavLink to="/pencils">Pencils</NavLink>
                    </li>
                </ul>
    */

    return (
        <div className="header">
            <div className="flex-container">
                <a href="/" className="logo">Art Supplies</a>
                <Settings
                isBox1Visible={isBox1Visible}
                toggleBoxVisibility={toggleBoxVisibility}
                handleCloseButtonClick={handleCloseButtonClick}
                shadowRef={shadowRef} />
            </div>
            <nav className="flex-container">
                <ul className="navbar">
                <li
                    className={`dropdown-1 ${isHovered1 ? 'active' : ''}`}
                    onMouseEnter={handleMouseEnter(1)}
                    onMouseLeave={handleMouseLeave(1)}>
                        <span>Brushes</span>
                        {isHovered1 && (
                            <ul className="dropdown-content-1">
                            <NavLink to="/brushes">Fan Brushes</NavLink>
                            </ul>
                        )}
                    </li>
                    <li
                    className={`dropdown-2 ${isHovered2 ? 'active' : ''}`}
                    onMouseEnter={handleMouseEnter(2)}
                    onMouseLeave={handleMouseLeave(2)}>
                        <span>Paint</span>
                        {isHovered2 && (
                            <ul className="dropdown-content-2">
                            <NavLink to="/paint">Fan Brushes</NavLink>
                            </ul>
                        )}
                    </li>
                    <li
                    className={`dropdown-3 ${isHovered3 ? 'active' : ''}`}
                    onMouseEnter={handleMouseEnter(3)}
                    onMouseLeave={handleMouseLeave(3)}>
                        <span>Paper</span>
                        {isHovered3 && (
                            <ul className="dropdown-content-3">
                            <NavLink to="/paper">Watercolor Paper</NavLink>
                            </ul>
                        )}
                    </li>
                    <li
                    className={`dropdown-4 ${isHovered4 ? 'active' : ''}`}
                    onMouseEnter={handleMouseEnter(4)}
                    onMouseLeave={handleMouseLeave(4)}>
                        <span>Pencils</span>
                        {isHovered4 && (
                            <ul className="dropdown-content-4">
                            <NavLink to="/pencils">Fan Brushes</NavLink>
                            </ul>
                        )}
                    </li>
                </ul>

                <div className="btn-container">
                    <button className="menu-btn"><img src="icons/bars-solid.svg" alt="Menu Bars"></img><span>Menu</span></button>
                    <div className="shopping-btn">
                        <ShoppingCart
                        isBox2Visible={isBox2Visible}
                        toggleBoxVisibility={toggleBoxVisibility}
                        handleCloseButtonClick={handleCloseButtonClick} />
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default Header;
