import React, { useContext, useEffect, useRef } from 'react';
import Product from './Product';
import { CartContext } from './CartContext';

const ShoppingCart = ({ isBox2Visible, toggleBoxVisibility, handleCloseButtonClick }) => {
  const { cartItems, setCartItems } = useContext(CartContext);

  const initialTotalSum = cartItems.reduce((sum, item) => sum + item.price, 0);
  const formattedInitialTotalSum = parseInt(initialTotalSum).toString();

  const [totalSum, setTotalSum] = React.useState(formattedInitialTotalSum);

  useEffect(() => {
    const newTotalSum = cartItems.reduce((sum, item) => sum + item.price, 0);
    const formattedTotalSum = parseInt(newTotalSum).toString();
    setTotalSum(formattedTotalSum);
  }, [cartItems]);

  const removeItem = (index) => {
    const currentCartItems = [...cartItems];
    currentCartItems.splice(index, 1);
    setCartItems(currentCartItems);
  };

  return (
    <div className="shopping-cart">
      <button
        className="header-btn shopping-btn shopping-btn-layout"
        onClick={toggleBoxVisibility}
      >
        <img src="icons/bag-shopping-solid.svg" alt="Shopping Bag" className="icon shopping-btn" />
        <span className="shopping-btn"> Your Cart</span>
      </button>

      {isBox2Visible && (
        <div className="shopping-box pop-up" tabIndex="1">
          <div className="flex-container">
            <h3 className="your-items">Your Items:</h3>
            <button
              className="cart-close-btn cart-close-btn-layout"
              onClick={handleCloseButtonClick}
            >
              <img src="icons/xmark-solid.svg" alt="X Mark" className="icon cart-close-btn" />
              <span className="cart-close-btn"> Close Cart</span>
            </button>
          </div>
          {cartItems.length === 0 ? (
            <p className="cart-empty-msg">Your cart is empty</p>
          ) : (
            <>
              <div>
                {cartItems.map((item, index) => (
                  <div key={index} className="cart-flex-container">
                    <img
                      src={item.image}
                      alt={item.alt}
                      style={{ width: '100px', height: '85px' }}
                      className="cart-img"
                    />
                    <div className="cart-item-info">
                      {item.name}
                      <br />
                      Price: <b>{item.price} kr</b>
                    </div>
                    <button
                      className="cart-remove-btn"
                      onClick={() => removeItem(index)}
                    >
                      <img
                        src="icons/minus-solid.svg"
                        alt="Minus Mark"
                        className="icon cart-close-btn"
                      />
                      <span> Remove Item</span>
                    </button>
                  </div>
                ))}
              </div>
              <p className="total-sum">
                Total sum: <b>{totalSum}</b> kr
              </p>
              <button className="checkout-btn">
                <img
                  src="icons/cash-register-solid.svg"
                  alt="Cash Register"
                  className="icon shopping-btn"
                />
                <span> Checkout</span>
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ShoppingCart;
