import React, { useState, useRef } from "react";
import Settings from './Settings';
import ShoppingBag from './ShoppingBag';
import { NavLink } from "react-router-dom";

const Header = () => {
    const [isBoxVisible, setBoxVisibility] = useState(false);
    const shadowRef = useRef(null);
    const settingsBox = useRef(null);
    const shoppingBox = useRef(null);

    const toggleBoxVisibility = (settingsBox) => {
        setBoxVisibility((prevVisibility) => (prevVisibility === settingsBox ? null : settingsBox));

        if(isBoxVisible === settingsBox) { // om inställningarna är synliga läggs skuggningsboxen till
            shadowRef.current.classList.add("shadow-box");

        } else { // om inställningarna ej är synliga tas skuggningsboxen bort
            shadowRef.current.classList.remove("shadow-box");
        }
    };

    const handleCloseButtonClick = () => {
        setBoxVisibility(false);
        shadowRef.current.classList.remove("shadow-box");
    };

    return (
        <div className="header">
            <div className="flex-container">
                <a href="/" className="logo">Art Supplies</a>
                <Settings
                isBoxVisible={isBoxVisible}
                toggleBoxVisibility={toggleBoxVisibility}
                handleCloseButtonClick={handleCloseButtonClick}
                shadowRef={shadowRef}
                settingsBox={settingsBox} />
            </div>
            <nav>
                <ul className="navbar">
                    <li>
                        <NavLink to="/brushes">Brushes</NavLink>
                    </li>
                    <li>
                        <NavLink to="/paint">Paint</NavLink>
                    </li>
                    <li>
                        <NavLink to="/paper">Paper</NavLink>
                    </li>
                    <li>
                        <NavLink to="/pencils">Pencils</NavLink>
                    </li>
                </ul>
                <div className="btn-container">
                    <button className="menu-btn"><img src="icons/bars-solid.svg" alt="Menu Bars"></img><span>Menu</span></button>
                    <ShoppingBag
                    isBoxVisible={isBoxVisible}
                    toggleBoxVisibility={toggleBoxVisibility}
                    handleCloseButtonClick={handleCloseButtonClick}
                    shoppingBox={shoppingBox} />
                </div>
            </nav>
        </div>
    )
}

export default Header;
