import React, { createContext, useContext, useState } from 'react';
const CartContext = createContext();

const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);
    const [counters, setCounters] = useState({});
    const [isBox2Visible, setBox2Visibility] = useState(false);

    const toggleBoxVisibility = () => {
        setBox2Visibility((prevVisibility) => !prevVisibility);
    }

    const addToCart = (item) => {
        const isItemInCart = cartItems.some(cartItem => cartItem.id === item.id);

        if (!isItemInCart) {    // om produkten precis lades till för första gången
            setCartItems((prevItems) => [...prevItems, item]);
            setCounters((prevCounters) => ({...prevCounters, [item.id]: (prevCounters[item.id] || 0) + 1}));

            setBox2Visibility(true);

        } else {    // om produkten redan fanns i varukorgen
            setCounters((prevCounters) => ({...prevCounters, [item.id]: (prevCounters[item.id] || 0) + 1}));
        }
    };

    const contextValues = {
        cartItems,
        setCartItems,
        counters,
        setCounters,
        addToCart,
        isBox2Visible,
        toggleBoxVisibility,
    };

    return <CartContext.Provider value={contextValues}>{children}</CartContext.Provider>;

};

const useCart = () => {
    const context = useContext(CartContext);
    if(!context) {
        throw new Error("useCart must be used within a CartProvider");
    }
    return context;
}

